[DSCLocalConfigurationManager()]
configuration ReportClientConfig
{
    Node localhost
    {
        Settings
        {
            RefreshMode          = 'Pull'
            RefreshFrequencyMins = 30 
            RebootNodeIfNeeded   = $true
        }

        ConfigurationRepositoryWeb CONTOSO-PullSrv
        {
            ServerURL          = 'https://CONTOSO-PULL:8080/PSDSCPullServer.svc'
            RegistrationKey    = 'bbb9778f-43f2-47de-b61e-a0daff474c6d'
            ConfigurationNames = @('ClientConfig')
        }

        ReportServerWeb CONTOSO-ReportSrv
        {
            ServerURL               = 'http://CONTOSO-REPORT:8080/PSDSCReportServer.svc'
            RegistrationKey         = 'ba39daaa-96c5-4f2f-9149-f95c46460faa'
            AllowUnsecureConnection = $true
        }
    }
}
PullClientConfigID

function GetStatusData { param ($Report) $statusData = $Report.StatusData | ConvertFrom-Json
$Resources = $statusData.ResourcesInDesiredState
Foreach ($Resource in $Resources) { Write-Host 'ResourceId: ' $Resource.ResourceId Write-Host 'SourceInfo: ' $Resource.SourceInfo Write-Host 'ModuleName: ' $Resource.ModuleName Write-Host 'ModuleVersion: ' $Resource.ModuleVersion Write-Host 'ConfigurationName: ' $Resource.ConfigurationName Write-Host 'ResourceName: ' $Resource.ResourceName Write-Host } }
